import pandas as pd
import json
import re
from bs4 import BeautifulSoup
from typing import Dict, Any, List, Optional

from pyparsing import Union

def clean_html_content(html_content: str) -> str:
    """
    Remove CSS styles and clean HTML content
    """
    # Remove style tags and their content
    html_content = re.sub(r'<style[^>]*>.*?</style>', '', html_content, flags=re.DOTALL | re.IGNORECASE)
    
    # Remove inline style attributes
    html_content = re.sub(r'\s*style\s*=\s*["\'][^"\']*["\']', '', html_content, flags=re.IGNORECASE)
    
    # Remove data-mce attributes
    html_content = re.sub(r'\s*data-mce-[^=]*\s*=\s*["\'][^"\']*["\']', '', html_content, flags=re.IGNORECASE)
    
    # Clean up extra whitespace
    html_content = re.sub(r'\s+', ' ', html_content)
    html_content = re.sub(r'>\s+<', '><', html_content)
    
    return html_content.strip()

def extract_text_from_element(element) -> str:
    """
    Extract clean text from BeautifulSoup element
    """
    if element is None:
        return ""
    text = element.get_text(strip=True)
    # Clean up special characters and extra spaces
    text = re.sub(r'\s+', ' ', text)
    text = text.replace('â‚¬', '€')
    return text.strip()

def extract_loan_data(soup: BeautifulSoup) -> Dict[str, Any]:
    """
    Extract structured loan data from the HTML
    """
    data = {
        "document_info": {},
        "borrower": {},
        "co_borrower": {},
        "household": {},
        "professional_situation": {},
        "income": {},
        "existing_credits": [],
        "patrimony": [],
        "project": {},
        "financing_plan": {},
        "monthly_payments": {},
        "insurance": {},
        "guarantees": {}
    }
    
    # Extract document information from header
    header = soup.find('header')
    if header:
        date_elem = header.find(text=re.compile(r'Date'))
        if date_elem:
            date_parent = date_elem.parent if date_elem.parent else date_elem
            date_text = extract_text_from_element(date_parent)
            date_match = re.search(r'(\d{2}/\d{2}/\d{4})', date_text)
            if date_match:
                data["document_info"]["date"] = date_match.group(1)
        
        ref_elem = header.find(text=re.compile(r'Réf.affaire'))
        if ref_elem:
            ref_parent = ref_elem.parent if ref_elem.parent else ref_elem
            ref_text = extract_text_from_element(ref_parent)
            ref_match = re.search(r'Réf\.affaire\s*:\s*([^\s]+)', ref_text)
            if ref_match:
                data["document_info"]["reference"] = ref_match.group(1)
    
    # Extract borrower information
    borrower_table = soup.find('th', string=re.compile(r'Emprunteur'))
    if borrower_table:
        table = borrower_table.find_parent('table')
        if table:
            rows = table.find_all('tr')
            for row in rows:
                cells = row.find_all(['td', 'th'])
                if len(cells) >= 2:
                    key = extract_text_from_element(cells[0]).lower()
